import React from 'react';
import './index.scss';

import Concurent from './concurent'

export default class Card extends React.Component {
    state = {
        show_all: false
    }

    showAll = () => {
        this.setState({
            show_all: !this.state.show_all
        })
    }

    render() {
        const { data } = this.props,
        { name, inn, ogrn , site, okved, okved_full, adres, description, logo, concurents} = data
        return(
            <div className = "Card">
                <div className = "minicard">
                    <div className="left-side">
                        <div className = "left-header">
                            <img className="profile-img" src={ require(`../../../assets/${ logo }`)} alt="1"></img>

                            <div className = "description">
                                <div className = "title">{ name }</div>
                                <p>ИНН: { inn }</p> <p>ОГРН: { ogrn }</p>
                            </div>
                        </div>
                        <div className = "cat-holder">
                            <div className="cat-switcher active">Основное</div>
                            <div className="cat-switcher"></div>
                            <div className="cat-switcher">category 3</div>
                        </div>
                        <div className = "cat-content">
                            <div className = "main">
                                <p>Сайт: { site }</p>
                                <p>ОКВЭД: { okved }</p>
                                <p>ОКВЭД(полн.ф): { okved_full }</p>
                                <p>Адрес: { adres }</p>
                                <p>Описание: { description } </p>
                            </div>
                        </div>

                    </div>
                    <div className = "right-side">
                        {
                            concurents.length >= 1 ? concurents.map((item, i) => {
                                return <Concurent key = {i} data = {item}></Concurent>
                            }) : (<p>No concurents</p>)
                        }
                    </div>
                </div>
                <div className = {`all-info ${ this.state.show_all ? "active" : "" }`}>
                    <div className = "info block-1">
                        <div className = "title">Категория Окунь</div>

                    </div>
                    <div className = "info block-2">
                        <div className = "title">Категория Вобла</div>

                    </div>
                </div>

            </div>
        )
    }
}
